INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '073a0596-dd3a-4da6-bdb8-17d893133e50', 'Back-end', 'NodeJs, Express, MySql');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '76ad3239-b7b8-4367-81db-643254ce31cf', 'en', 'An application for electronic restaurant menus with augmented reality elements. This innovative solution enhances the dining experience by allowing customers to access the menu via a QR code, view detailed information about each dish, and see a 3D model of the dish in augmented reality.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'c69d3615-9a2d-43fc-8e17-a6fb8103aedc', 'ru', 'Это приложение для электронных меню с элементами дополненной реальности, предназначенное для улучшения взаимодействия клиентов с меню ресторанов. Привлекательный и удобный интерфейс позволяет погрузиться в мир гастрономических изысков и сделать правильный выбор блюда.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'a8fdcaea-6c59-4ae9-a989-1912e0fca5c6', 'ja', 'レストラン向けの電子メニューアプリで、拡張現実 (AR) の要素が含まれています。これにより、飲食店は、より効果的なプレゼンテーションと顧客エンゲージメントを提供できます。');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'e4ba6971-1178-4d61-a83a-589808a1d01f', 'de', 'Eine Anwendung für elektronische Restaurantmenüs mit erweiterter Realitätselementen. Diese innovative Lösung verbessert das Esserlebnis, indem sie den Kunden ermöglicht, das Menü über einen QR-Code abzurufen, detaillierte Informationen über jedes Gericht einzusehen und ein 3D-Modell des Gerichts in erweiterter Realität zu sehen.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '94b71f79-64db-403c-8b51-4f6ea9b06abb', 'tr', 'Artırılmış gerçeklik öğeleri içeren elektronik restoran menüleri için bir uygulama. Bu yenilikçi çözüm, müşterilerin menüye bir QR kodu aracılığıyla erişmesine, her yemek hakkında ayrıntılı bilgileri görüntülemesine ve yemeğin artırılmış gerçeklikte 3D modelini görmesine olanak tanıyarak yemek deneyimini geliştirir.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '5c0575b9-0232-41d6-be4c-fd845c0b128c', 'fr', 'Une application de menus de restaurant électroniques avec des éléments de réalité augmentée. Cette solution innovante améliore l''expérience culinaire en permettant aux clients d''accéder au menu via un code QR, de consulter des informations détaillées sur chaque plat et de voir un modèle 3D du plat en réalité augmentée.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '6ccb5c36-d696-4a77-908a-c73b4d5254af', 'ja', 'QRコードを使用したレストランメニューの表示：顧客は、QRコードをスキャンすることで簡単にレストランのメニューにアクセスできます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '1f748188-dc32-4124-9410-6db4059829a6', 'ja', '個々の料理とその説明、写真、調理時間、材料の表示：アプリは、各料理の詳細情報を提供し、顧客が料理を選ぶ際に役立ちます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'c1a13b32-4514-4b86-9cb2-1c92e2c44ced', 'ja', '拡張現実における3Dモデルの表示：AR技術を利用して、顧客は料理の3Dモデルを表示し、料理の見た目や大きさを事前に確認することができます,。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '4ad19bc1-74fd-471c-b4d3-aa436444d9de', 'en', 'Access the restaurant menu through a QR code: Customers can quickly and easily access the menu by scanning a QR code with their smartphone, eliminating the need for physical menus.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '094485f7-7983-449a-9a9f-8eea1c8e0b4b', 'en', 'View individual dishes with descriptions, photos, preparation time, and ingredients: The application provides detailed information about each dish, including descriptions, high-quality photos, preparation time, and a list of ingredients, so customers know exactly what to expect.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '29188d7c-119d-48c5-96a1-eea5a26c56d3', 'en', 'Augmented reality 3D model of dishes: The app takes the dining experience to the next level by allowing customers to view a 3D model of the dish in augmented reality, giving them a better understanding of the dish''''s appearance and portion size before ordering.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '90b5c55b-1c3f-465c-a869-8a2a40c51ead', 'ru', 'Быстрый доступ к меню ресторана: С помощью QR-кода клиенты могут легко и быстро получить доступ к меню ресторана, избегая контакта с физическими меню.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '776e0e61-b223-42cf-a252-6d2fddd92114', 'ru', 'Подробная информация о блюде: Приложение предоставляет информацию о каждом блюде, включая описание, фотографии, время приготовления и ингредиенты. Это помогает клиентам сделать осознанный выбор и учитывать свои индивидуальные предпочтения и диетические ограничения.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'dee8111a-a2c0-46bd-9e2b-7cedcb051322', 'ru', '3D-модели блюд с использованием дополненной реальности: Уникальная функция AR позволяет клиентам рассмотреть трехмерные модели блюд прямо на их столе. Это не только увлекательный опыт, но и дает возможность клиентам представить внешний вид блюда, его размер и порцию перед заказом.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '00cee6fd-e182-4534-a0b4-3f06462a3c89', 'de', 'Zugang zum Restaurantmenü über einen QR-Code: Kunden können das Menü schnell und einfach abrufen, indem sie mit ihrem Smartphone einen QR-Code scannen, wodurch die Notwendigkeit von physischen Menüs entfällt.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '7b68faa0-c989-458f-a69e-1d720e8d37cb', 'de', 'Einzelne Gerichte mit Beschreibungen, Fotos, Zubereitungszeit und Zutaten anzeigen: Die Anwendung bietet detaillierte Informationen zu jedem Gericht, einschließlich Beschreibungen, hochwertigen Fotos, Zubereitungszeit und einer Liste der Zutaten, sodass Kunden genau wissen, was sie erwarten können.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '4a184382-a5b3-4743-9db9-474942b34289', 'de', 'Erweiterte Realität 3D-Modell von Gerichten: Die App hebt das Esserlebnis auf die nächste Stufe, indem sie Kunden ermöglicht, ein 3D-Modell des Gerichts in erweiterter Realität zu betrachten und so vor der Bestellung ein besseres Verständnis für das Aussehen und die Portionsgröße des Gerichts zu erhalten.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'c84d49f9-e08e-4eb7-815b-eb7dde3047e8', 'tr', 'QR kodu ile restoran menüsüne erişin: Müşteriler, akıllı telefonlarıyla bir QR kodu tarayarak hızlı ve kolay bir şekilde menüye erişebilir, fiziksel menülere olan ihtiyacı ortadan kaldırır.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'bc713d98-4fe7-4bd3-a7fa-7ca0570becbe', 'tr', 'Açıklamaları, fotoğrafları, hazırlık süresi ve içerikleri ile bireysel yemekleri görüntüleyin: Uygulama, her yemeğin ayrıntılı bilgilerini sunar, açıklamalar, yüksek kaliteli fotoğraflar, hazırlık süresi ve içerik listesi dahil olmak üzere, böylece müşteriler ne bekleyeceklerini tam olarak bilir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'e45bacfa-a69d-44c5-8252-c31c7c1acdca', 'tr', 'Yemeklerin artırılmış gerçeklik 3D modeli: Uygulama, müşterilerin yemeğin artırılmış gerçeklikte 3D modelini görüntülemelerine olanak tanıyarak yemek deneyimini bir üst seviyeye çıkarır ve böylece yemeğin görünümü ve porsiyon büyüklüğü hakkında daha iyi bir anlayış sağlar.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'd89e952c-0a94-4aad-a4c4-65f70409340b', 'fr', 'Accéder au menu du restaurant via un code QR : Les clients peuvent accéder rapidement et facilement au menu en scannant un code QR avec leur smartphone, éliminant ainsi le besoin de menus physiques.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'b20a2c5f-116c-49e9-b6ca-891a25284d11', 'fr', 'Voir les plats individuels avec descriptions, photos, temps de préparation et ingrédients : L''''application fournit des informations détaillées sur chaque plat, y compris les descriptions, des photos de haute qualité, le temps de préparation et une liste d''''ingrédients, afin que les clients sachent exactement à quoi s''''attendre.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '48dc358e-b347-4a1f-9c08-1def0753d96b', 'fr', 'Modèle 3D en réalité augmentée des plats : L''''application améliore l''''expérience culinaire en permettant aux clients de visualiser un modèle 3D du plat en réalité augmentée, leur donnant une meilleure compréhension de l''''apparence et de la taille des portions avant de commander.');
