INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'aac81af4-2fec-4e01-a293-d3332afb51f5', 'Mobile', 'React Native');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '85ec6325-0ddc-4217-b6dd-461f08bc380c', 'en', 'A platform that develops entrepreneurial skills through online game-based solutions. BUSINESS UP focuses on building development trajectories, supporting business communities, and participating in cyber championships to foster a comprehensive learning experience for aspiring entrepreneurs.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '5073717a-6a4c-48c9-8f16-0be672c75125', 'ru', 'Business Up - это интеллектуальная платформа, созданная для развития предпринимательских навыков и формирования успешных бизнес-траекторий. С помощью инновационных игровых решений, дистанционного обучения и цифрового взаимодействия с бизнес-сообществом, Business Up подготавливает будущих предпринимателей к успешному старту и развитию своего бизнеса.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'a88566e7-720f-42d5-9ccf-c42ad4912ccf', 'ja', 'BUSINESS UPは、オンラインゲームソリューションを利用して起業家のスキルを育成するプラットフォームです。開発トラジェクトリー、ビジネスコミュニティのサポート、サイバーチャンピオンシップへの参加を通じて、起業家のスキルを向上させます。');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'ccfbb078-5691-48d3-a79a-fb8a6a264550', 'de', 'Eine Plattform, die unternehmerische Fähigkeiten durch Online-Spiel-basierte Lösungen entwickelt. BUSINESS UP konzentriert sich auf den Aufbau von Entwicklungsverläufen, die Unterstützung von Geschäftsgemeinschaften und die Teilnahme an Cyber-Meisterschaften, um ein umfassendes Lernerlebnis für angehende Unternehmer zu fördern.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'cb98942f-973f-43f9-bd8a-2b2bed9389a0', 'tr', 'Çevrimiçi oyun tabanlı çözümler aracılığıyla girişimcilik becerilerini geliştiren bir platform. BUSINESS UP, gelişim yörüngeleri oluşturma, iş topluluklarını destekleme ve siber şampiyonalara katılma üzerine yoğunlaşarak, gelecekteki girişimciler için kapsamlı bir öğrenme deneyimi sunar.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '4bec8f50-1bdd-4d5f-a197-497193378732', 'fr', 'Une plateforme qui développe des compétences entrepreneuriales grâce à des solutions basées sur des jeux en ligne. BUSINESS UP se concentre sur la construction de trajectoires de développement, le soutien aux communautés d''affaires et la participation à des championnats cybernétiques pour offrir une expérience d''apprentissage complète aux entrepreneurs en herbe.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '1217e5f5-15cd-41fb-9beb-e2c29a884ee3', 'ja', '評価と評判が表示される個人ダッシュボード：ユーザーは、プラットフォーム内での進捗状況、評価、評判を追跡できる独自のダッシュボードを持っています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '5e84fe5f-50a2-467f-b689-44824c906bec', 'ja', 'ビジネスゲームとチャンピオンシップ：魅力的なビジネスゲームとチャンピオンシップが、ユーザーが起業家としてのスキルを磨き、競争力を高める楽しい環境を提供します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '8112fb33-c117-421f-b223-6f3d4a502b88', 'ja', '遠隔学習：プラットフォームは遠隔学習の機会を提供し、ユーザーが教育リソースにアクセスし、起業家としての知識を拡大することができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'd333b648-deec-4af1-8c69-b55520e0e49e', 'ja', 'デジタルビジネスコミュニティ：BUSINESS UPは、ユーザーがネットワークを構築し、協力し、同じ志を持つ個人とアイデアを交換できるデジタルビジネスコミュニティを育成します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'e8aa5b1b-e25a-437b-966b-bac15c8171d8', 'ja', '社会貢献プロジェクト：プラットフォームには、社会貢献プロジェクトのコンポーネントが含まれており、ユーザーが起業家としてのスキルを磨きながら、社会にポジティブな影響を与えることができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '0c6ff142-0c51-46a8-974c-9f1c3b88a7c7', 'en', 'Personal dashboard with ratings and reputation: Users have their own dashboard where they can track their progress, ratings, and reputation within the platform.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'f3b1a253-2b02-4d31-ba54-2dc02ca6cf04', 'en', 'Business games and championships: Engaging business games and championships provide a competitive and fun environment for users to develop and hone their entrepreneurial skills.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '1f48d759-f04e-4321-9476-496ddefc907f', 'en', 'Remote learning: The platform offers distance learning opportunities, enabling users to access educational resources and expand their knowledge in the field of entrepreneurship.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'bd8f1f2e-30dc-4280-8e3c-a81c2434fd3c', 'en', 'Digital business community: BUSINESS UP fosters a digital business community where users can network, collaborate, and exchange ideas with like-minded individuals.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '8864afeb-4060-4a46-aa2d-14360b35dcab', 'en', 'Social project: The platform includes a social project component, allowing users to make a positive impact on society while refining their entrepreneurial skills.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '369566da-3774-4c83-8525-13e2d999c5e9', 'ru', 'Личный кабинет с рейтингом и репутацией: Отслеживайте свой прогресс и соревнуйтесь с другими участниками платформы, занимая приоритетные места в рейтинге и улучшая репутацию.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '5531a27d-04b6-4520-9dce-8c9c89485935', 'ru', 'Бизнес-игры и чемпионаты: Участвуйте в увлекательных бизнес-играх и киберчемпионатах, развивая свои навыки и получая возможность применить их на практике.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '18876de9-7225-4c78-9572-18896b123351', 'ru', 'Дистанционное обучение: Получайте доступ к высококачественным образовательным материалам и курсам, которые помогут вам освоить ключевые компетенции в сфере предпринимательства.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '81ec13de-780b-4bf7-851e-25c6fe97d1f6', 'ru', 'Цифровое бизнес-коммьюнити: Взаимодействуйте с другими предпринимателями и экспертами в рамках платформы, обменивайтесь опытом и идеями для успешного развития своего бизнеса.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '0670252a-39b1-4aed-9bc0-bdab1664c42e', 'ru', 'Социальный проект: Вовлекайтесь в реализацию социальных проектов, которые способствуют улучшению жизни окружающих вас людей и общества в целом.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '2715cf31-4c3e-4765-b470-e889a728d9bb', 'de', 'Persönliches Dashboard mit Bewertungen und Reputation: Benutzer verfügen über ein eigenes Dashboard, auf dem sie ihren Fortschritt, ihre Bewertungen und ihren Ruf innerhalb der Plattform verfolgen können.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '8b1b33ee-8c32-4f3e-946b-a179234db01f', 'de', 'Business-Spiele und Meisterschaften: Spannende Business-Spiele und Meisterschaften bieten eine wettbewerbsfähige und unterhaltsame Umgebung, in der Benutzer ihre unternehmerischen Fähigkeiten entwickeln und verfeinern können.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'ea3deff5-a3cc-43b1-ac40-d2d3f73b241d', 'de', 'Fernlernen: Die Plattform bietet Möglichkeiten zum Fernlernen und ermöglicht es Benutzern, auf Bildungsressourcen zuzugreifen und ihr Wissen im Bereich Unternehmertum zu erweitern.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '580d49b6-20f5-4e7c-97cb-959bb08a4bce', 'de', 'Digitale Geschäftsgemeinschaft: BUSINESS UP fördert eine digitale Geschäftsgemeinschaft, in der Benutzer Kontakte knüpfen, zusammenarbeiten und Ideen mit Gleichgesinnten austauschen können.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '24622b08-a7ee-465d-bfbf-cbecb58547e4', 'de', 'Soziales Projekt: Die Plattform beinhaltet eine soziale Projekt-Komponente, die es Benutzern ermöglicht, einen positiven Einfluss auf die Gesellschaft zu nehmen und gleichzeitig ihre unternehmerischen Fähigkeiten zu verfeinern.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'b2aa40d8-aa36-4e89-8405-3794663f8b4f', 'tr', 'Puan ve itibar içeren kişisel kontrol paneli: Kullanıcıların, platformdaki ilerlemelerini, puanlarını ve itibarını takip edebilecekleri kendi kontrol paneli bulunmaktadır.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '0a501107-f8d7-48ca-950a-d82b33964b5b', 'tr', 'İş oyunları ve şampiyonalar: Kullanıcıların girişimcilik becerilerini geliştirmeleri ve mükemmelleştirmeleri için rekabetçi ve eğlenceli bir ortam sunan etkileyici iş oyunları ve şampiyonaları.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '196af4dc-a851-484b-acad-7a70a3ca847f', 'tr', 'Uzaktan öğrenme: Platform, kullanıcıların eğitim kaynaklarına erişebilmelerini ve girişimcilik alanındaki bilgilerini genişletmelerini sağlayan uzaktan öğrenme fırsatları sunar.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '34752608-4e8a-4bfc-a9ba-574dae556cd2', 'tr', 'Dijital iş topluluğu: BUSINESS UP, kullanıcıların benzer düşünen bireylerle iletişim kurabileceği, işbirliği yapabileceği ve fikir alışverişinde bulunabileceği dijital bir iş topluluğunu teşvik eder.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'e68ad861-6304-4ad6-827b-f748e6bd6a9b', 'tr', 'Sosyal proje: Platform, kullanıcıların girişimcilik becerilerini geliştirirken toplum üzerinde olumlu bir etki yapmalarını sağlayan bir sosyal proje bileşenini içerir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '69594656-3b9a-4c59-93fd-b44563dc7ade', 'fr', 'Tableau de bord personnel avec notes et réputation : Les utilisateurs disposent de leur propre tableau de bord où ils peuvent suivre leur progression, leurs notes et leur réputation au sein de la plateforme.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '237b8f2d-e605-4c3a-9650-f103be3c2df5', 'fr', 'Jeux d''''affaires et championnats : Des jeux d''''affaires captivants et des championnats offrent un environnement compétitif et amusant pour les utilisateurs afin de développer et d''''affiner leurs compétences entrepreneuriales.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '321060d8-2958-4947-a23a-0e95ac0c43a8', 'fr', 'Apprentissage à distance : La plateforme propose des opportunités d''''apprentissage à distance, permettant aux utilisateurs d''''accéder à des ressources éducatives et d''''étendre leurs connaissances dans le domaine de l''''entrepreneuriat.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'eb0d5eba-edcb-4549-910b-ba99c52fd830', 'fr', 'Communauté d''''affaires numérique : BUSINESS UP favorise une communauté d''''affaires numérique où les utilisateurs peuvent réseauter, collaborer et échanger des idées avec des personnes partageant les mêmes idées.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '54da96a2-86b5-4425-87bd-b3a7b5b5138f', 'fr', 'Projet social : La plateforme comprend un volet de projet social, permettant aux utilisateurs d''''avoir un impact positif sur la société tout en affinant leurs compétences entrepreneuriales.');
