INSERT INTO subtitles( project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '01ff04f4-fccd-4fa7-8103-6c90d27cbfda', 'ja', 'ビジネスアップ-ゲームを通してあなたの起業家のスキルを開発');
