INSERT INTO subtitles( project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '1d4a4826-c721-4032-b028-138df9d1a2c6', 'tr', 'Platform, çevrimiçi oyun çözümleri aracılığıyla girişimcilik becerilerini geliştirir.');
