INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'a6ca24c5-07c8-4543-a9e1-6f288d8945f2', 'Back-end', 'GoLang, Gin, Gorm, PostgreSQL');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '8cbce4a6-3d9a-44cc-9b47-5c1fd2e90937', 'en', 'A service assistant for sellers on Ozon and Wildberries platforms. OWSUP is designed to optimize sales performance by providing a variety of helpful features, allowing sellers to streamline their operations and maximize profits.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '14d317c2-7c48-42ce-9830-32fbbef84a48', 'ru', 'OWSUP - это интуитивный и удобный сервис-помощник для продавцов на платформах Ozon и Wildberries. Сервис разработан с целью оптимизации продаж и упрощения управления аккаунтами продавцов на обеих площадках. OWSUP предоставляет продавцам все необходимые инструменты и аналитику для успешного ведения бизнеса в сфере электронной коммерции.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '9e67a9a5-d7f8-4fcd-a19f-0d22e4f30555', 'ja', 'オゾンとワイルドベリーズのプラットフォームでの販売者向けのサービスアシスタント。OWSUPは、さまざまな便利な機能を提供することで、販売者が業務を効率化し、利益を最大化することを目的とした販売パフォーマンスの最適化を行います。');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '86f7cdb7-27c5-490a-a22d-7f8a6711a4f5', 'de', 'Ein Serviceassistent für Verkäufer auf den Plattformen Ozon und Wildberries. OWSUP wurde entwickelt, um die Verkaufsleistung zu optimieren, indem eine Vielzahl hilfreicher Funktionen bereitgestellt wird, die es Verkäufern ermöglichen, ihre Abläufe zu straffen und den Gewinn zu maximieren.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'bcd533e2-1b4c-41dc-9265-ffd000853626', 'tr', 'Ozon ve Wildberries platformlarındaki satıcılar için bir hizmet asistanı. OWSUP, satıcıların işlemlerini düzenlemelerine ve karlarını en üst düzeye çıkarmalarına olanak tanıyan çeşitli yardımcı özellikler sunarak satış performansını optimize etmek için tasarlanmıştır.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '288b519a-8339-4a77-8f38-5b963bdd71f6', 'fr', 'Un assistant de service pour les vendeurs sur les plateformes Ozon et Wildberries. OWSUP est conçu pour optimiser les performances de vente en fournissant une variété de fonctionnalités utiles, permettant aux vendeurs de rationaliser leurs opérations et de maximiser leurs profits.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '49161bd4-58aa-4681-baa4-588eb6de9640', 'ja', 'アカウント間または販売プラットフォーム間での商品カードのコピー：OWSUPを使用すると、異なるアカウントやプラットフォーム間で商品カードを簡単にコピーでき、複数のストアフロントの管理が簡単になります。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '22cc9b91-9b26-4e71-b7c6-3288d476c348', 'ja', 'すべてのアカウントの統合分析ダッシュボード：このサービスでは、すべてのユーザーアカウントからのデータを統合した包括的な分析ダッシュボードを提供し、さまざまなプラットフォームでの販売パフォーマンスを監視する便利な方法を提供します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '54d281c3-bcf4-48e5-96eb-538efcb28bf4', 'ja', 'すべてのアカウントからの注文分析：OWSUPは、リンクされたすべてのアカウントからの注文データを集約し、販売者が販売履歴を追跡し、トレンドを特定するのが簡単になります。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '57b919cc-6aaa-48f8-b575-4750063764c9', 'ja', '商品配送およびその他の手数料の利益および手数料計算機：このサービスには、販売者が利益を算出し、コミッションや配送費用などのさまざまな手数料を考慮するのに役立つ内蔵の計算機が含まれています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '66c69cf0-e8ce-44ca-b26c-bc3d5f1d5e73', 'en', 'Copying product cards between accounts or sales platforms: OWSUP allows users to easily copy product cards between different accounts or platforms, simplifying the process of managing multiple storefronts.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '9a117027-29c3-48b2-b47e-097bcd6a2cd6', 'en', 'Unified analytics dashboard for all accounts: The service provides a comprehensive analytics dashboard that consolidates data from all user accounts, offering a convenient way to monitor sales performance across various platforms.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '3de5f23d-a144-434b-a592-682cd52b7405', 'en', 'Order analytics from all accounts: OWSUP aggregates order data from all linked accounts, making it easy for sellers to track their sales history and identify trends.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'e2e680a3-dda1-47a4-83df-a86a525e0bb5', 'en', 'Profit and commission calculators for product delivery and other fees: The service includes built-in calculators to help sellers determine their profits and account for various fees, such as commission and delivery costs.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'fda77da2-5e29-4c09-887a-fc4a97f63b51', 'ru', 'Копирование карточек товаров между кабинетами или платформами: Благодаря интеграции с Ozon и Wildberries, OWSUP позволяет легко копировать и перемещать карточки товаров между различными аккаунтами и платформами, экономя ваше время и усилия.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'b6377072-d9ef-41cc-859f-e657f16c687c', 'ru', 'Общая аналитика всех кабинетов в одном Dashboard: OWSUP предоставляет удобный обзор всех ваших аккаунтов на Ozon и Wildberries в одной панели управления, облегчая анализ и сравнение продаж.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '7257a51b-3c7c-4129-a303-16a002118714', 'ru', 'Аналитика заказов со всех кабинетов: Получайте подробную аналитику заказов с обеих платформ, что позволит вам лучше понимать предпочтения и ожидания вашей целевой аудитории, а также принимать своевременные решения для повышения эффективности продаж.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '77b69693-76dd-4b31-bc76-d621485cbd9c', 'ru', 'Калькуляторы прибыли и комиссии товара: OWSUP включает в себя инструменты для расчета прибыли и комиссий, связанных с доставкой товаров и другими аспектами ведения бизнеса на платформах Ozon и Wildberries. Это позволяет продавцам точно оценить свою рентабельность и определить оптимальные стратегии ценообразования.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'aa0cea81-6088-4fae-8f4b-151e10ff6706', 'de', 'Kopieren von Produktkarten zwischen Konten oder Verkaufsplattformen: OWSUP ermöglicht es Benutzern, Produktkarten einfach zwischen verschiedenen Konten oder Plattformen zu kopieren und vereinfacht so das Verwalten mehrerer Verkaufsflächen.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'f9e80fd1-b6b2-4c9f-a0d2-0f06b25902b6', 'de', 'Einheitliches Analyse-Dashboard für alle Konten: Der Service bietet ein umfassendes Analyse-Dashboard, das Daten aus allen Benutzerkonten zusammenfasst und eine praktische Möglichkeit bietet, die Verkaufsleistung auf verschiedenen Plattformen zu überwachen.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '89fccbc6-2621-4781-bfe6-f94402030c81', 'de', 'Bestellanalytics von allen Konten: OWSUP fasst Bestelldaten aus allen verknüpften Konten zusammen, sodass es für Verkäufer einfach ist, ihre Verkaufshistorie zu verfolgen und Trends zu erkennen.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '5ac30139-60fb-49b0-88db-137d754f0e81', 'de', 'Gewinn- und Provisionsrechner für Produktlieferungen und andere Gebühren: Der Service beinhaltet integrierte Rechner, die Verkäufern helfen, ihre Gewinne zu ermitteln und verschiedene Gebühren, wie Provisionen und Lieferkosten, zu berücksichtigen.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '67841dcd-a3bf-4b46-8ca9-ca7912613c15', 'tr', 'Ürün kartlarını hesaplar veya satış platformları arasında kopyalama: OWSUP, kullanıcıların ürün kartlarını farklı hesaplar veya platformlar arasında kolayca kopyalamalarını sağlayarak, birden fazla vitrin yönetme sürecini basitleştirir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'ebc3c5c8-3bbd-4a37-ae94-fba6c1cfe620', 'tr', 'Tüm hesaplar için birleşik analitik gösterge paneli: Hizmet, tüm kullanıcı hesaplarından verileri birleştiren kapsamlı bir analitik gösterge paneli sunarak, çeşitli platformlarda satış performansını izleme için uygun bir yol sunar.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'fa266450-7065-404d-bed9-b4a84ad28dbf', 'tr', 'Tüm hesaplardan sipariş analitiği: OWSUP, bağlantılı tüm hesaplardan sipariş verilerini toplar, satıcıların satış geçmişini takip etmelerini ve eğilimleri belirlemelerini kolaylaştırır.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '8e2a9269-8d1c-4085-a74a-5ceda9f6b3b2', 'tr', 'Ürün teslimatı ve diğer ücretler için kar ve komisyon hesaplayıcıları: Hizmet, satıcıların karlarını belirlemelerine ve komisyon ve teslimat maliyetleri gibi çeşitli ücretlere göre hesaplamalarına yardımcı olacak dahili hesaplayıcılar içerir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'd459fbab-f18d-4eaa-a54c-448ec5c89204', 'fr', 'Copie des fiches produits entre comptes ou plateformes de vente : OWSUP permet aux utilisateurs de copier facilement les fiches produits entre différents comptes ou plateformes, simplifiant ainsi la gestion de plusieurs points de vente.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '31aeb7cb-db7b-4e08-b011-292ef8e30993', 'fr', 'Tableau de bord d''''analyse unifié pour tous les comptes : Le service propose un tableau de bord d''''analyse complet qui regroupe les données de tous les comptes utilisateurs, offrant un moyen pratique de surveiller les performances de vente sur différentes plateformes.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '48f0ad33-0ae1-445e-ab44-cf8a6698217a', 'fr', 'Analyse des commandes de tous les comptes : OWSUP agrège les données de commandes de tous les comptes liés, facilitant ainsi le suivi de l''''historique des ventes et l''''identification des tendances pour les vendeurs.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '36deb02e-20e5-4d2d-b46f-084362318b5c', 'fr', 'Calculatrices de profit et de commission pour la livraison des produits et autres frais : Le service inclut des calculatrices intégrées pour aider les vendeurs à déterminer leurs profits et à tenir compte des divers frais, tels que les commissions et les coûts de livraison.');
