INSERT INTO projects (uuid, photo_url, title, categories) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'epicherision/preview', 'EPICHERISION 2.0', '3');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'dea3d8d6-abe0-4a2f-874e-caccf299dc4c', 'Bot', 'NodeJs, node-html-parser');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'c79aa797-880f-4c20-820c-1ce41175ec8c', 'en', 'A Discord bot designed to provide information about newly arrived products. EPICHERISION is equipped with a variety of parsers for collecting data and generating ATK links, making it a valuable resource for users looking to stay informed about the latest product offerings.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '6a1c32f9-548e-4017-a2d5-7bcb1ad638da', 'ru', 'EPICHERISION - это интеллектуальный Discord бот, созданный для предоставления информации о поступлении новых товаров в различных магазинах. Благодаря мощным парсерам и гибким настройкам, EPICHERISION обеспечивает своевременные и релевантные оповещения, которые помогут пользователям быть в курсе последних новинок и успеть приобрести желанные товары.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '299a6ab1-dae2-4c52-853f-c8741c8b4bc1', 'ja', '新しく入荷した製品に関する情報を提供するために設計されたDiscordボット。EPICHERISIONは、データ収集とATKリンク生成のためのさまざまなパーサを備えており、最新の製品情報を追い求めるユーザーにとって貴重なリソースとなります。');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'c10cdc64-d549-4901-9947-4b0c53b09e4b', 'de', 'Ein Discord-Bot, der entwickelt wurde, um Informationen über neu eingetroffene Produkte zu liefern. EPICHERISION ist mit einer Vielzahl von Parsern zum Sammeln von Daten und Generieren von ATK-Links ausgestattet, was ihn zu einer wertvollen Ressource für Benutzer macht, die über die neuesten Produktangebote informiert bleiben möchten.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '1e1b48e5-612c-4a50-a389-d95e155cd8d6', 'tr', 'Yeni gelen ürünler hakkında bilgi sağlamak için tasarlanmış bir Discord botu. EPICHERISION, veri toplamak ve ATK bağlantıları oluşturmak için çeşitli ayrıştırıcılarla donatılmıştır, bu da kullanıcıların en son ürün teklifleri hakkında bilgi sahibi olmalarını sağlayan değerli bir kaynak haline getirir.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'f213834a-6148-4ca3-a161-0b591498bda6', 'fr', 'Un bot Discord conçu pour fournir des informations sur les nouveaux produits arrivés. EPICHERISION est équipé d''une variété de parseurs pour collecter les données et générer des liens ATK, ce qui en fait une ressource précieuse pour les utilisateurs cherchant à rester informés des dernières offres de produits.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '624871b8-bc50-49c5-a3d5-e82ab8fc2a09', 'ja', 'データとメタデータ用の幅広いパーサ：EPICHERISIONは、ATKリンク生成に必要な標準データとメタデータを収集するための多数のパーサを搭載しており、ユーザーが正確で最新の情報を受け取ることができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'd2734646-72b5-40fb-b9b3-eac6077714ed', 'ja', 'カスタマイズ可能なキーワードとカテゴリ選択：ユーザーは、製品選択のためのキーワードとカテゴリを編集でき、ボットの通知を自分の特定の興味や好みに合わせることができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '26dd96a9-0a22-4c8b-af43-461aa91c4820', 'ja', '各ストアごとのチャンネル固有の通知：ボットは各ストアごとに別々のチャンネルに製品の更新情報を送信し、ユーザーが必要な情報に簡単にアクセスし、追跡できるようにします。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '4ce0d2b7-7230-4776-b368-94326581895f', 'ja', 'NikeのオンデマンドATKリンク生成：EPICHERISIONは、管理者がアクティベーションすると、Nike製品のATKリンクを生成できる機能を備えており、ユーザーはショッピング体験をさらにコントロールできます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'd8fb8d21-b413-4b44-87ec-98c4a5c46027', 'en', 'Extensive parsers for data and metadata: EPICHERISION is built with numerous parsers to gather both standard data and metadata necessary for generating ATK links, ensuring that users receive accurate and up-to-date information.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'e2ddc4b4-e829-484f-9392-fe97e21317d8', 'en', 'Customizable keyword and category selection: Users can edit keywords and categories for product selection, allowing them to tailor the bot''''s notifications to their specific interests and preferences.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '7eebed9e-cd4e-4066-9743-2e14436970eb', 'en', 'Channel-specific notifications for each store: The bot sends product updates to separate channels for each store, ensuring that users can easily access and track the information they need.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'b04d07b9-4add-4574-83e2-d92966268c8f', 'en', 'On-demand ATK link generation for Nike: EPICHERISION is capable of generating ATK links for Nike products upon administrator activation, providing users with even greater control over their shopping experience.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '6c00ee61-f5c3-4747-8bc4-f7d37c376938', 'ru', 'Мощные парсеры данных: В процессе реализации EPICHERISION было создано множество парсеров, обрабатывающих как обычные, так и метаданные, необходимые для генерации АТК ссылок.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'a0ce1b7a-508e-4a89-b156-4faddcc042ce', 'ru', 'Гибкая настройка ключевых слов и категорий: Пользователи имеют возможность редактировать ключевые слова и категории товаров, которые они хотят отслеживать, получая только релевантные оповещения.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '7d3a70f8-7d33-4eb1-92e2-d8acdabcb1cb', 'ru', 'Отдельные каналы для разных магазинов: Оповещения о новых товарах отправляются в отдельные каналы для каждого магазина, обеспечивая удобную и структурированную информацию.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '44320afc-4761-46fa-9532-3c565a588f43', 'ru', 'Генерация АТК ссылок для Nike: EPICHERISION предоставляет специализированный функционал для генерации атк ссылок для магазина Nike, активируемый администратором сервера.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '83c31371-83c1-43e9-92e3-e26a20402eb4', 'de', 'Umfangreiche Parser für Daten und Metadaten: EPICHERISION ist mit zahlreichen Parsern ausgestattet, um sowohl Standarddaten als auch Metadaten zu sammeln, die für die Generierung von ATK-Links erforderlich sind, und stellt sicher, dass Benutzer genaue und aktuelle Informationen erhalten.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'c44c4463-825c-4e2a-9e0d-61c634d04503', 'de', 'Anpassbare Stichwort- und Kategorieauswahl: Benutzer können Stichworte und Kategorien für die Produktauswahl bearbeiten, sodass sie die Benachrichtigungen des Bots an ihre speziellen Interessen und Vorlieben anpassen können.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'c1796390-8e61-4e12-a19c-c544b765c748', 'de', 'Kanalspezifische Benachrichtigungen für jeden Laden: Der Bot sendet Produktaktualisierungen an separate Kanäle für jeden Laden, um sicherzustellen, dass Benutzer die benötigten Informationen leicht abrufen und verfolgen können.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'a11f5b68-3565-4af1-a07c-6375254b468b', 'de', 'ATK-Link-Generierung auf Anfrage für Nike: EPICHERISION kann ATK-Links für Nike-Produkte bei Aktivierung durch den Administrator generieren und bietet Benutzern so noch mehr Kontrolle über ihr Einkaufserlebnis.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'db3a36a0-c573-463e-ac15-a753aa16bb77', 'tr', 'Veri ve meta veri için kapsamlı ayrıştırıcılar: EPICHERISION, kullanıcıların doğru ve güncel bilgilere ulaşmasını sağlamak için ATK bağlantıları oluşturmak için gerekli olan hem standart verileri hem de meta verileri toplamak için çok sayıda ayrıştırıcıyla inşa edilmiştir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'c5b1188d-4636-4f3b-b6d2-7c84819a599a', 'tr', 'Özelleştirilebilir anahtar kelime ve kategori seçimi: Kullanıcılar, ürün seçimi için anahtar kelimeleri ve kategorileri düzenleyebilir, böylece botun bildirimlerini belirli ilgi alanlarına ve tercihlerine göre uyarlayabilirler.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '389a6d19-0adc-47c8-aa48-111395d0fe55', 'tr', 'Her mağaza için kanala özel bildirimler: Bot, kullanıcıların ihtiyaç duydukları bilgilere kolayca erişebilmelerini ve takip edebilmelerini sağlamak için her mağaza için ayrı kanallara ürün güncellemeleri gönderir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '7ed395d0-4f97-4cf7-ba8a-c1bc59ce4367', 'tr', 'Nike için talep üzerine ATK bağlantısı oluşturma: EPICHERISION, yönetici aktivasyonu üzerine Nike ürünleri için ATK bağlantıları oluşturma yeteneğine sahiptir, kullanıcılara alışveriş deneyimlerinde daha fazla kontrol sağlar.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '69793890-8696-442c-893c-b765147ff012', 'fr', 'Parseurs étendus pour les données et les métadonnées : EPICHERISION est construit avec de nombreux parseurs pour collecter à la fois les données standard et les métadonnées nécessaires à la génération de liens ATK, garantissant ainsi que les utilisateurs reçoivent des informations précises et à jour.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'a2706aad-968c-44f9-b90d-36fe2b23fe67', 'fr', 'Sélection personnalisable de mots-clés et de catégories : Les utilisateurs peuvent modifier les mots-clés et les catégories pour la sélection des produits, leur permettant d''''adapter les notifications du bot à leurs intérêts et préférences spécifiques.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'bf8fcdc4-774f-4a99-8a85-c495cb46c8b7', 'fr', 'Notifications spécifiques à chaque chaîne pour chaque magasin : Le bot envoie des mises à jour de produits sur des chaînes séparées pour chaque magasin, garantissant que les utilisateurs peuvent accéder et suivre facilement les informations dont ils ont besoin.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'bd8d5a1e-fda4-4b8b-876c-05c05b1ffe21', 'fr', 'Génération de liens ATK à la demande pour Nike : EPICHERISION est capable de générer des liens ATK pour les produits Nike lors de l''''activation par l''''administrateur, offrant aux utilisateurs un contrôle encore plus grand sur leur expérience d''''achat.');
