INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '8355d52a-8cf2-48e1-a4a7-9be22210d941', 'Front-end', 'Vue, Vue Router');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'b0467970-9723-41ac-af75-dce88e32d543', 'Back-end', 'NodeJs, Express, MySQL');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '6d7b4a6c-2c3f-4d92-92cf-fa4adb9ae3b4', 'en', 'A web platform for EMIAS company employees, designed to facilitate efficient task management and provide real-time updates on task statuses. The platform streamlines workflows and fosters collaboration among team members, enabling them to work more effectively and achieve their goals.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '20576f5e-57d1-4a19-aa72-eaf312cb54b0', 'ru', 'EMIAS - это уникальная веб-платформа, разработанная специально для сотрудников компании EMIAS. Платформа предоставляет интуитивные инструменты для эффективного управления задачами, отслеживания прогресса команды и предложения идей. EMIAS также включает в себя систему наград и значков, поощряющую активность и достижения сотрудников.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '1712b91f-b38b-4f22-a291-1c7ad7a24673', 'ja', 'EMIAS社の従業員向けに設計されたウェブプラットフォームは、効率的なタスク管理を促進し、タスクのステータスに関するリアルタイムの更新を提供します。プラットフォームはワークフローを合理化し、チームメンバー間の協力を促進し、彼らがより効果的に働き、目標を達成することができるようにします。');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'cd729189-84f8-4b65-b168-adb302636713', 'de', 'Eine Webplattform für Mitarbeiter des Unternehmens EMIAS, die darauf abzielt, ein effizientes Aufgabenmanagement zu ermöglichen und Echtzeit-Updates zu Aufgabenstatus bereitzustellen. Die Plattform optimiert Arbeitsabläufe und fördert die Zusammenarbeit zwischen Teammitgliedern, sodass sie effektiver arbeiten und ihre Ziele erreichen können.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '8ccba227-e47b-4ca3-8349-93767a5dc319', 'tr', 'EMIAS şirketi çalışanları için tasarlanmış, etkili görev yönetimini kolaylaştırmak ve görev durumlarına ilişkin gerçek zamanlı güncellemeler sağlamak amacıyla oluşturulan bir web platformu. Platform, iş akışlarını düzenler ve ekip üyeleri arasında işbirliğini teşvik eder, böylece daha etkili çalışmalarını ve hedeflerine ulaşmalarını sağlar.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '4c1e025f-fbe6-4e3f-b471-6cafb2d8b4ef', 'fr', 'Une plateforme web pour les employés de l''entreprise EMIAS, conçue pour faciliter la gestion efficace des tâches et fournir des mises à jour en temps réel sur l''état des tâches. La plateforme rationalise les flux de travail et favorise la collaboration entre les membres de l''équipe, leur permettant de travailler plus efficacement et d''atteindre leurs objectifs.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '58c39ea3-7e7f-4d4e-98c6-0fbf8fca17eb', 'ja', '従業員のためのパーソナルダッシュボード：EMIASは、各従業員のパーソナルダッシュボードを備えており、タスクを管理し、整理された状態を維持することができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '270bb67c-1ba5-4682-8736-9d81cff47022', 'ja', 'アイデア提出機能：チームメンバーは自分のアイデアを提案することができ、協力的で革新的な作業環境を促進します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'c7408859-0c6a-4f79-a2a9-04593528b123', 'ja', 'チームの進捗状況の追跡：従業員はチームの進捗状況を監視し、プロジェクト内の最新の動向に常に最新情報を保つことができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'bc53da27-d9d6-403d-9c22-5243f6ba5d55', 'ja', '功績のあるユーザーにバッジを授与する：プラットフォームは、管理者が特別なバッジを授与することで、優れた従業員を認識し、報われることができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '9d349077-0031-4841-ae95-614236ac7691', 'en', 'Personal dashboard for employees: EMIAS features a personal dashboard for each employee, allowing them to manage their tasks and stay organized.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'bed5a9b6-9867-47e2-aeae-3d1900fdde72', 'en', 'Idea submission capability: Team members can propose their own ideas, fostering a collaborative and innovative work environment.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '0b70cd14-16a1-47c2-ad45-c920c00fe61e', 'en', 'Team progress tracking: Employees can monitor their team''''s progress and stay up-to-date on the latest developments within their projects.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'f0e63167-f0a2-4bb2-8e9f-5a914b01fc48', 'en', 'Awarding badges to deserving users: The platform allows administrators to recognize and reward outstanding employees by granting them special badges.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '8ad009a2-79de-4f81-9eae-c5d796dd7cb2', 'ru', 'Личный кабинет для сотрудников: Каждый сотрудник имеет доступ к своему личному кабинету, где он может управлять своими задачами, отслеживать выполнение и следить за актуальным статусом работы.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'b5973ec7-9b9a-42d4-89e6-fbdef9973920', 'ru', 'Возможность предлагать идеи: Сотрудники могут предлагать свои идеи для улучшения процессов, продуктов или услуг компании, обсуждать их с коллегами и реализовывать наиболее перспективные предложения.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '3e75bc6a-48d0-4f86-a4d8-04dbf294d972', 'ru', 'Просмотр прогресса команды: Платформа предоставляет возможность просматривать общий прогресс команды, отслеживать выполнение задач и сравнивать результаты разных подразделений.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '14036cd0-197b-4a5c-b9bf-51c9de3d4364', 'ru', 'Система наград и значков: EMIAS включает систему значков, которые могут быть присвоены сотрудникам за особые достижения, активность или вклад в развитие компании. Это способствует мотивации и вовлеченности персонала.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '1e3c4719-39c5-4806-9ea0-b6e83ce6801a', 'de', 'Persönliches Dashboard für Mitarbeiter: EMIAS bietet jedem Mitarbeiter ein persönliches Dashboard, mit dem sie ihre Aufgaben verwalten und organisiert bleiben können.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '48138224-f8a6-401e-9d11-d7672736a6b8', 'de', 'Ideen-Einreichungsfunktion: Teammitglieder können eigene Ideen vorschlagen und so eine kollaborative und innovative Arbeitsumgebung fördern.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'a0435b8c-6907-4e9f-8a83-4f5159d5ee06', 'de', 'Team-Fortschrittsverfolgung: Mitarbeiter können den Fortschritt ihres Teams überwachen und über die neuesten Entwicklungen in ihren Projekten auf dem Laufenden bleiben.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'a54b3ca6-97d6-4708-964c-caca72566244', 'de', 'Verleihung von Abzeichen an verdiente Benutzer: Die Plattform ermöglicht es Administratoren, herausragende Mitarbeiter durch die Vergabe von besonderen Abzeichen zu würdigen und zu belohnen.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '69b15944-a9fc-413a-9785-8074513c3bbc', 'tr', 'Çalışanlar için kişisel kontrol paneli: EMIAS, çalışanların görevlerini yönetmelerine ve organize kalmalarına olanak tanıyan her çalışan için kişisel bir kontrol paneli sunar.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '72d676c5-2873-4666-8d6d-23affefe4cd8', 'tr', 'Fikir sunma yeteneği: Ekip üyeleri kendi fikirlerini önererek işbirlikçi ve yenilikçi bir çalışma ortamını teşvik edebilir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '428748e8-4c14-4a19-abfe-8ffdd6310e0f', 'tr', 'Takım ilerlemesini takip etme: Çalışanlar, takımlarının ilerlemesini izleyebilir ve projelerindeki en son gelişmeler konusunda güncel kalabilir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '19fea266-324e-4383-a5d5-09e99e144f12', 'tr', 'Hak eden kullanıcılara rozet verme: Platform, yöneticilerin başarılı çalışanları özel rozetler vererek tanımalarına ve ödüllendirmelerine olanak tanır.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '2c68aa51-43e5-4f61-8826-c97a1763bca6', 'fr', 'Tableau de bord personnel pour les employés : EMIAS dispose d''''un tableau de bord personnel pour chaque employé, ce qui leur permet de gérer leurs tâches et de rester organisés.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'c01c22e0-cf43-4ae7-a9b5-af58d7d53ced', 'fr', 'Capacité de soumission d''''idées : Les membres de l''''équipe peuvent proposer leurs propres idées, favorisant un environnement de travail collaboratif et innovant.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '103b2f06-bac7-4990-8ed7-7ae0b45ee563', 'fr', 'Suivi de la progression de l''''équipe : Les employés peuvent suivre la progression de leur équipe et rester informés des derniers développements au sein de leurs projets.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '8f5797cc-5e54-4c93-83bb-1a160b862ea6', 'fr', 'Attribution de badges aux utilisateurs méritants : La plateforme permet aux administrateurs de reconnaître et de récompenser les employés exceptionnels en leur accordant des badges spéciaux.');
