INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', 'eeb76b35-dbbc-4059-a110-345435f8b28f', 'Front-end', 'NextJs, i18n');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '07f8cb05-4dda-422d-8273-b3063212e615', 'en', 'A multilingual website built using Next.js for a company. U.Business features an admin panel for easy text editing, providing a user-friendly experience for both site administrators and visitors.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '4727c833-1354-472a-ab3d-eeb293f74a10', 'ru', 'U.Business - это современный и функциональный сайт, разработанный на next.js для компании, предоставляющий мультиязычность и удобную админ-панель для редактирования текста. Сайт обеспечивает гибкость и адаптивность, позволяя компании обращаться к международному рынку с помощью интуитивно понятного и простого в управлении веб-ресурса.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '22072b88-d6ea-4637-9170-f30b2c48ddee', 'ja', '企業向けにNext.jsを使用して構築された多言語ウェブサイトです。U.Businessは、テキスト編集が簡単な管理パネルを備えており、サイト管理者と訪問者の両方にとって使いやすい体験を提供します。');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '6b4a426d-fb3f-497e-945a-12fded4c6215', 'de', 'Eine mehrsprachige Website, die mit Next.js für ein Unternehmen erstellt wurde. U.Business verfügt über ein Admin-Panel für einfache Textbearbeitung und bietet eine benutzerfreundliche Erfahrung für Administratoren und Besucher der Website.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '2d8e26bb-169a-421d-81eb-1501eac85ecb', 'tr', 'Bir şirket için Next.js kullanılarak oluşturulan çok dilli bir web sitesi. U.Business, hem site yöneticileri hem de ziyaretçiler için kullanıcı dostu bir deneyim sunan, kolay metin düzenleme için bir yönetici paneline sahiptir.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '653f4c02-6a77-4e91-895c-7dba7290e62e', 'fr', 'Un site web multilingue construit en utilisant Next.js pour une entreprise. U.Business dispose d''un panneau d''administration pour faciliter la modification du texte, offrant une expérience conviviale pour les administrateurs du site et les visiteurs.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '22b2b146-0319-40e5-8ada-8290f8fc2298', 'ja', 'テキスト編集用の管理パネル：U.Businessには、ウェブサイトのコンテンツが最新かつ関連性のある状態を維持できるように、シンプルなテキスト編集が可能な管理パネルが含まれています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '96779128-06ae-4008-9893-877435de5506', 'ja', 'i18nを使用した多言語対応：ウェブサイトはi18nで構築されており、複数の言語をシームレスにサポートし、多様なユーザーに対応しています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', 'cf2a306b-e912-45ff-b854-e72e2a0e2ddb', 'en', 'Admin panel for text editing: U.Business includes an admin panel that allows for simple text editing, ensuring that the website''''s content remains up-to-date and relevant.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', 'ca5729e1-9da7-4ac3-8d13-8a4762255b65', 'en', 'Multilingual support with i18n: The website is built with i18n, enabling seamless support for multiple languages and catering to a diverse range of users.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '586d485d-bd6b-4b57-bbc5-a42a4cda26f1', 'ru', 'Админ-панель для редактирования текста: U.Business предоставляет удобную и простую в использовании админ-панель, которая позволяет редактировать и обновлять текст на сайте без необходимости обращаться к разработчикам.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', 'dcb5e75b-e02e-4062-bf15-f826ae3462c6', 'ru', 'Мультиязычность с помощью i18n: Благодаря интеграции с библиотекой i18n, сайт поддерживает мультиязычность и позволяет компании легко добавлять новые языковые версии, адаптируясь к потребностям международного рынка.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', 'c1c21ecd-ba8a-4bfb-95d4-ad5cb95445f4', 'de', 'Admin-Panel zur Textbearbeitung: U.Business beinhaltet ein Admin-Panel, das eine einfache Textbearbeitung ermöglicht und sicherstellt, dass der Inhalt der Website aktuell und relevant bleibt.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '790e900e-8ba7-4b05-a984-8704d6e14e86', 'de', 'Mehrsprachige Unterstützung mit i18n: Die Website ist mit i18n erstellt, wodurch eine nahtlose Unterstützung für mehrere Sprachen ermöglicht wird und eine vielfältige Benutzergruppe bedient wird.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '5bc84078-3f9c-4c76-864f-c84cea1f96e3', 'tr', 'Metin düzenleme için yönetici paneli: U.Business, web sitesi içeriğinin güncel ve ilgi çekici kalmasını sağlayan, basit metin düzenlemeye olanak tanıyan bir yönetici paneli içerir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', 'd5919af7-2983-48e2-9c98-66f44c2fe8fb', 'tr', 'i18n ile çok dilli destek: Web sitesi, i18n ile oluşturulmuştur ve bu sayede birden fazla dilde sorunsuz destek sağlar ve çeşitli kullanıcılara hitap eder.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '5adea7f7-b780-43ae-815d-314ba7d71f22', 'fr', 'Panneau d''''administration pour la modification du texte : U.Business comprend un panneau d''''administration qui permet une modification simple du texte, garantissant que le contenu du site reste à jour et pertinent.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '1f7ef58a-b5e5-4d0b-aa1c-14b20c1ce933', 'fr', 'Support multilingue avec i18n : Le site web est construit avec i18n, permettant une prise en charge transparente de plusieurs langues et répondant aux besoins d''''une large gamme d''''utilisateurs.');
