INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'eb45ead3-9887-407c-8b73-8f546e4bf08e', 'Front-end', 'Vue 2, VueRouter');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '1200da36-e55e-4194-b986-520a725936d9', 'Back-end', 'Python 3, Flask, MySQL');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '7b2738ce-9cef-4822-84d9-377a96388c98', 'en', 'A "Secret Santa" game designed for the CROC company. This traditional Christmas game involves anonymous gift exchanges among a group of participating individuals, bringing fun and festive cheer to the workplace.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '289d0457-ac91-4984-ac30-de523c4c6123', 'ru', 'Secret Santa - это онлайн-версия традиционной рождественской игры "Тайный Санта" для компании Croc. Цель игры - анонимный обмен подарками между участниками, работающими в разных отделах и специализациях. Secret Santa предоставляет удобный личный кабинет для управления всеми аспектами игры, а также интеллектуальный алгоритм подбора пар и рассылку писем на почту участников.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'd79d8d08-54e1-4512-b52c-e4cf56716815', 'ja', 'CROC社向けにデザインされた「シークレットサンタ」というゲームです。この伝統的なクリスマスゲームは、参加者が匿名でプレゼントを交換することで、職場に楽しさと祝祭の喜びをもたらします。');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'efc073b0-5cb6-4965-916f-ea167af795dd', 'de', 'Ein "Wichteln"-Spiel, das für das Unternehmen CROC entwickelt wurde. Bei diesem traditionellen Weihnachtsspiel tauschen die Teilnehmer anonym Geschenke untereinander aus und bringen so Spaß und festliche Stimmung an den Arbeitsplatz.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'be1e34b1-f08a-40ec-b77b-1493ee9b7132', 'tr', 'CROC şirketi için tasarlanmış bir "Secret Santa" oyunu. Bu geleneksel Noel oyunu, katılan bireyler arasında anonim hediye değişimini içerir ve iş yerine eğlence ve neşe getirir.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'bdedf158-2f17-47cc-b6b0-3b625ae4c913', 'fr', 'Un jeu de "Père Noël Secret" conçu pour l''entreprise CROC. Ce jeu traditionnel de Noël implique des échanges de cadeaux anonymes entre un groupe de participants, apportant ainsi de la joie et de l''ambiance festive sur le lieu de travail.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '5385a322-ce4c-4306-9b70-9e8f1d39a44b', 'ja', '多様なペアリングを作成するアルゴリズム：このゲームでは、独自のアルゴリズムを使用して、異なる専門分野や部門の参加者同士のペアを作成し、すべてのプレイヤーに多様でエンゲージングな体験を提供します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'bacc45ee-3c2d-4912-a569-10c3690dd53f', 'ja', 'メール通知：シークレットサンタはメール通知を送信して参加者に情報を提供し、ゲーム全体を通じてスムーズなコミュニケーションが行われるようにします。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '249bd32c-0c85-487b-9e53-08b783eb1eb5', 'ja', '個人用ダッシュボード機能：すべてのゲーム機能は、各参加者の個人用ダッシュボードからアクセスできるため、プレイヤーがゲームへの関与を簡単に管理できます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '354a75b2-c9e8-45bd-8aa8-0e7d01235b9f', 'en', 'Algorithm for creating diverse pairings: The game uses a unique algorithm to create pairs of participants from different specializations and departments, ensuring a diverse and engaging experience for all players.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '4555a5fe-ba61-43ab-a789-67694cb615ee', 'en', 'Email notifications: Secret Santa sends out email notifications to keep participants informed and ensure smooth communication throughout the game.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'dddd05b7-c400-4825-a0ba-a998a2782d73', 'en', 'Personal dashboard features: All game functions can be accessed through each participant''''s personal dashboard, making it easy for players to manage their involvement in the game.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '8974e9ea-9c58-4368-bed4-9adc88ab848e', 'ru', 'Интеллектуальный алгоритм подбора пар: Secret Santa использует алгоритм, который создает пары для обмена подарками, учитывая разные специализации и отделы участников. Это способствует интерактивности и интеграции сотрудников компании');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '6648d659-f3da-4c4c-b8a1-6acacdfafe73', 'ru', 'Рассылка писем на почту: Все уведомления об игре, включая информацию о вашем "Тайном Санте" и его предпочтениях, отправляются участникам на электронную почту.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'f62114aa-f45f-493c-ad12-a4aedd5db2d4', 'ru', 'Личный кабинет: Secret Santa предоставляет удобный личный кабинет, в котором участники могут управлять своим участием в игре, просматривать информацию о своем "Тайном Санте" и получать уведомления о событиях.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '13cef0ea-4afa-4b5a-b4d6-836175852376', 'de', 'Algorithmus zur Erstellung vielfältiger Paarungen: Das Spiel verwendet einen einzigartigen Algorithmus, um Paare von Teilnehmern aus verschiedenen Fachgebieten und Abteilungen zu bilden und so eine abwechslungsreiche und ansprechende Erfahrung für alle Spieler zu gewährleisten.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'e82df98b-9000-4456-99cf-771e3877c8f1', 'de', 'E-Mail-Benachrichtigungen: Wichteln verschickt E-Mail-Benachrichtigungen, um die Teilnehmer auf dem Laufenden zu halten und eine reibungslose Kommunikation während des Spiels zu gewährleisten.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '68d1999d-4ef9-4fcb-be6d-99bce683d755', 'de', 'Persönliche Dashboard-Funktionen: Alle Spielfunktionen können über das persönliche Dashboard jedes Teilnehmers aufgerufen werden, was es den Spielern erleichtert, ihre Beteiligung am Spiel zu verwalten.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'c5a65937-73a9-4788-9604-6239839fb010', 'tr', 'Çeşitli eşleşmeler oluşturma algoritması: Oyun, farklı uzmanlık alanlarından ve bölümlerden katılımcı çiftleri oluşturmak için benzersiz bir algoritma kullanarak tüm oyuncular için çeşitli ve ilgi çekici bir deneyim sunar.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '0299cf74-631c-4feb-b377-487ba6ca042b', 'tr', 'E-posta bildirimleri: Gizli Noel Baba, katılımcıları bilgilendirmek ve oyun boyunca sorunsuz iletişim sağlamak için e-posta bildirimleri gönderir.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'c0baf7fc-69cd-4819-a5c0-d9e032e0121c', 'tr', 'Kişisel gösterge paneli özellikleri: Tüm oyun işlevleri, her katılımcının kişisel gösterge panelinden erişilebilir, böylece oyuncuların oyun içindeki katılımlarını yönetmelerini kolaylaştırır.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '56586907-2f1d-40ab-92b0-03112c030ebb', 'fr', 'Algorithme de création de paires diverses : Le jeu utilise un algorithme unique pour créer des paires de participants de différentes spécialisations et départements, garantissant une expérience diversifiée et engageante pour tous les joueurs.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '2eaf882c-374d-492f-90f8-b124918bc306', 'fr', 'Notifications par e-mail : Père Noël Secret envoie des notifications par e-mail pour tenir les participants informés et assurer une communication fluide tout au long du jeu.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '3f14bbe3-0735-4e59-834d-3e52df5b6f9f', 'fr', 'Fonctionnalités personnelles du tableau de bord : Toutes les fonctions du jeu peuvent être accessibles via le tableau de bord personnel de chaque participant, ce qui facilite la gestion de leur participation au jeu.');
