INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '1667b5ce-e8a4-4bf2-a03d-f01ccb6b3ac6', 'Back-end', 'NodeJs, Express, MySql');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '46665051-88f1-4129-af86-ce799687aac2', 'en', 'An application for electronic restaurant menus with augmented reality elements. This innovative solution enhances the dining experience by allowing customers to access the menu via a QR code, view detailed information about each dish, and see a 3D model of the dish in augmented reality.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '7362d175-9cf5-4ef3-ae96-2bc6d0bb3add', 'ru', 'Это приложение для электронных меню с элементами дополненной реальности, предназначенное для улучшения взаимодействия клиентов с меню ресторанов. Привлекательный и удобный интерфейс позволяет погрузиться в мир гастрономических изысков и сделать правильный выбор блюда.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'c048f12a-99d1-4845-bf68-428af8dc0d7c', 'ja', 'レストラン向けの電子メニューアプリで、拡張現実 (AR) の要素が含まれています。これにより、飲食店は、より効果的なプレゼンテーションと顧客エンゲージメントを提供できます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '1affe255-b034-43be-bdab-8b1fb1e4d601', 'ja', 'QRコードを使用したレストランメニューの表示：顧客は、QRコードをスキャンすることで簡単にレストランのメニューにアクセスできます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'd8c21095-4026-46d8-94b6-6d12f235a32e', 'ja', '個々の料理とその説明、写真、調理時間、材料の表示：アプリは、各料理の詳細情報を提供し、顧客が料理を選ぶ際に役立ちます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '909f79c6-f646-429c-a03d-400fa32bac60', 'ja', '拡張現実における3Dモデルの表示：AR技術を利用して、顧客は料理の3Dモデルを表示し、料理の見た目や大きさを事前に確認することができます,。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'e26491fe-8560-4fef-828f-656d28d6131e', 'en', 'Access the restaurant menu through a QR code: Customers can quickly and easily access the menu by scanning a QR code with their smartphone, eliminating the need for physical menus.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '33c35bf9-1d38-49e1-b035-773ac664ff24', 'en', 'View individual dishes with descriptions, photos, preparation time, and ingredients: The application provides detailed information about each dish, including descriptions, high-quality photos, preparation time, and a list of ingredients, so customers know exactly what to expect.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'b55873a2-c786-4bd0-81ba-f493a955672b', 'en', 'Augmented reality 3D model of dishes: The app takes the dining experience to the next level by allowing customers to view a 3D model of the dish in augmented reality, giving them a better understanding of the dish''s appearance and portion size before ordering.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', 'dd5d3587-37c2-4324-8974-922ff92c63f8', 'ru', 'Быстрый доступ к меню ресторана: С помощью QR-кода клиенты могут легко и быстро получить доступ к меню ресторана, избегая контакта с физическими меню.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '88e14fd6-ef8e-4503-b9b0-0cc8c1b7086e', 'ru', 'Подробная информация о блюде: Приложение предоставляет информацию о каждом блюде, включая описание, фотографии, время приготовления и ингредиенты. Это помогает клиентам сделать осознанный выбор и учитывать свои индивидуальные предпочтения и диетические ограничения.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('81ee526d-beb0-4a71-a192-e2b5a9886146', '72ec0864-3f36-4c96-809f-ee385bfbd22c', 'ru', '3D-модели блюд с использованием дополненной реальности: Уникальная функция AR позволяет клиентам рассмотреть трехмерные модели блюд прямо на их столе. Это не только увлекательный опыт, но и дает возможность клиентам представить внешний вид блюда, его размер и порцию перед заказом.');
