INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'ea6fe84c-6838-45f7-803c-9fb9eefd6399', 'Back-end', 'Python, Flask, MySql');
