INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '3f06f019-33d6-4b92-ba25-82ca3dc177a7', 'en', 'A platform that develops entrepreneurial skills through online game-based solutions. BUSINESS UP focuses on building development trajectories, supporting business communities, and participating in cyber championships to foster a comprehensive learning experience for aspiring entrepreneurs.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '763989b0-7683-4353-bf68-dfc6324012aa', 'ru', 'Описание: Business Up - это интеллектуальная платформа, созданная для развития предпринимательских навыков и формирования успешных бизнес-траекторий. С помощью инновационных игровых решений, дистанционного обучения и цифрового взаимодействия с бизнес-сообществом, Business Up подготавливает будущих предпринимателей к успешному старту и развитию своего бизнеса.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '6a1d072b-9710-477e-bb92-2fd8af0808a5', 'ja', 'BUSINESS UPは、オンラインゲームソリューションを利用して起業家のスキルを育成するプラットフォームです。開発トラジェクトリー、ビジネスコミュニティのサポート、サイバーチャンピオンシップへの参加を通じて、起業家のスキルを向上させます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'bc48ec65-2127-4962-a1c9-82147bbb7f08', 'ja', '評価と評判が表示される個人ダッシュボード：ユーザーは、プラットフォーム内での進捗状況、評価、評判を追跡できる独自のダッシュボードを持っています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '9f89b3f8-4579-4739-a6fe-f81bf11249e2', 'ja', 'ビジネスゲームとチャンピオンシップ：魅力的なビジネスゲームとチャンピオンシップが、ユーザーが起業家としてのスキルを磨き、競争力を高める楽しい環境を提供します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'b2a8eb68-d767-45bc-8e71-8db19dbe8be0', 'ja', '遠隔学習：プラットフォームは遠隔学習の機会を提供し、ユーザーが教育リソースにアクセスし、起業家としての知識を拡大することができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '8e6f286c-120e-4bf3-bdea-75887ac15fe4', 'ja', 'デジタルビジネスコミュニティ：BUSINESS UPは、ユーザーがネットワークを構築し、協力し、同じ志を持つ個人とアイデアを交換できるデジタルビジネスコミュニティを育成します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'bb3de8ea-5aa6-49dc-94e9-32bd398786da', 'ja', '社会貢献プロジェクト：プラットフォームには、社会貢献プロジェクトのコンポーネントが含まれており、ユーザーが起業家としてのスキルを磨きながら、社会にポジティブな影響を与えることができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'dbec291b-0243-4b61-b37a-b40bd01a8a55', 'en', 'Personal dashboard with ratings and reputation: Users have their own dashboard where they can track their progress, ratings, and reputation within the platform.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'f80259cb-a6da-4b25-b617-f98c4e7a5035', 'en', 'Business games and championships: Engaging business games and championships provide a competitive and fun environment for users to develop and hone their entrepreneurial skills.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '7cee2856-15bc-4cc6-814d-cbe6574e8dab', 'en', 'Remote learning: The platform offers distance learning opportunities, enabling users to access educational resources and expand their knowledge in the field of entrepreneurship.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'b767455e-6136-479e-a857-1d5fddc1e857', 'en', 'Digital business community: BUSINESS UP fosters a digital business community where users can network, collaborate, and exchange ideas with like-minded individuals.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'eced4c67-c872-49be-9102-6aa2c8a7c077', 'en', 'Social project: The platform includes a social project component, allowing users to make a positive impact on society while refining their entrepreneurial skills.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '1d6936fc-5b1c-4870-830a-b5c9636d94de', 'ru', 'Личный кабинет с рейтингом и репутацией: Отслеживайте свой прогресс и соревнуйтесь с другими участниками платформы, занимая приоритетные места в рейтинге и улучшая репутацию.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '17580c7d-50d2-4481-be8b-7e38026272a2', 'ru', 'Бизнес-игры и чемпионаты: Участвуйте в увлекательных бизнес-играх и киберчемпионатах, развивая свои навыки и получая возможность применить их на практике.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '350f83bb-3a04-4b45-8393-bef548e35c96', 'ru', 'Дистанционное обучение: Получайте доступ к высококачественным образовательным материалам и курсам, которые помогут вам освоить ключевые компетенции в сфере предпринимательства.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'b1e59d19-c344-4b48-9277-4450522ac89b', 'ru', 'Цифровое бизнес-коммьюнити: Взаимодействуйте с другими предпринимателями и экспертами в рамках платформы, обменивайтесь опытом и идеями для успешного развития своего бизнеса.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', 'f6ac90e3-c5f4-4d17-a2d8-10706b231cf7', 'ru', 'Социальный проект: Вовлекайтесь в реализацию социальных проектов, которые способствуют улучшению жизни окружающих вас людей и общества в целом.');
