INSERT INTO subtitles( project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '5eb83fed-64aa-4792-b3f6-fe6b2ba6d6b7', 'en', 'The platform builds entrepreneurial skills through online gaming solutions.');
INSERT INTO subtitles( project_uuid, uuid, lang, content) VALUES ('0b1dc4ba-541c-49e2-8d67-46875a85ad9f', '6f2644fe-81bb-4325-95ef-38b658b5af66', 'ru', 'Business Up - develop your entrepreneurial skills through the game');
