INSERT INTO projects (uuid, photo_url, title, categories) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'OWSUP.jpg', 'OWSUP', '2');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'd3379dac-23c1-467c-b9a2-5c05f16bd44e', 'Front-end', 'React, Redux Toolkit, Ant.d');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '7a675521-f61e-4e52-84d1-41830503e7fc', 'Back-end', 'GoLang, Gin, Gorm, PostgreSQL');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '8041cc3d-3dbd-4091-9659-ed2e34d1e6b7', 'en', 'A service assistant for sellers on Ozon and Wildberries platforms. OWSUP is designed to optimize sales performance by providing a variety of helpful features, allowing sellers to streamline their operations and maximize profits.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'e465857c-e3cb-480b-9ef4-e775845bc20b', 'ru', 'OWSUP - это интуитивный и удобный сервис-помощник для продавцов на платформах Ozon и Wildberries. Сервис разработан с целью оптимизации продаж и упрощения управления аккаунтами продавцов на обеих площадках. OWSUP предоставляет продавцам все необходимые инструменты и аналитику для успешного ведения бизнеса в сфере электронной коммерции.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'b40899a0-eab9-408e-9e26-521d8eb67c32', 'ja', 'オゾンとワイルドベリーズのプラットフォームでの販売者向けのサービスアシスタント。OWSUPは、さまざまな便利な機能を提供することで、販売者が業務を効率化し、利益を最大化することを目的とした販売パフォーマンスの最適化を行います。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'd88e1648-18d6-4dea-97a0-11174355f102', 'ja', 'アカウント間または販売プラットフォーム間での商品カードのコピー：OWSUPを使用すると、異なるアカウントやプラットフォーム間で商品カードを簡単にコピーでき、複数のストアフロントの管理が簡単になります。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '9b935030-bc72-4798-a172-e1a29083036f', 'ja', 'すべてのアカウントの統合分析ダッシュボード：このサービスでは、すべてのユーザーアカウントからのデータを統合した包括的な分析ダッシュボードを提供し、さまざまなプラットフォームでの販売パフォーマンスを監視する便利な方法を提供します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '7cdb0b70-4cb8-417c-a8c5-afd5b5edf098', 'ja', 'すべてのアカウントからの注文分析：OWSUPは、リンクされたすべてのアカウントからの注文データを集約し、販売者が販売履歴を追跡し、トレンドを特定するのが簡単になります。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'efb35d49-3625-4ac2-a6cd-a1f42676dcca', 'ja', '商品配送およびその他の手数料の利益および手数料計算機：このサービスには、販売者が利益を算出し、コミッションや配送費用などのさまざまな手数料を考慮するのに役立つ内蔵の計算機が含まれています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '5512d078-78fd-4d01-8133-4bbe0cf8731e', 'en', 'Copying product cards between accounts or sales platforms: OWSUP allows users to easily copy product cards between different accounts or platforms, simplifying the process of managing multiple storefronts.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '6006ec6e-c0ca-416e-a5d9-36a61a7413e7', 'en', 'Unified analytics dashboard for all accounts: The service provides a comprehensive analytics dashboard that consolidates data from all user accounts, offering a convenient way to monitor sales performance across various platforms.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '96291225-ed72-469c-81f8-67e8fc6d3a6a', 'en', 'Order analytics from all accounts: OWSUP aggregates order data from all linked accounts, making it easy for sellers to track their sales history and identify trends.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '12a4b928-b879-40de-b4bc-b8b079cf4b74', 'en', 'Profit and commission calculators for product delivery and other fees: The service includes built-in calculators to help sellers determine their profits and account for various fees, such as commission and delivery costs.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', 'd69427d6-edd4-4a01-9dc8-e1321e76cc42', 'ru', 'Копирование карточек товаров между кабинетами или платформами: Благодаря интеграции с Ozon и Wildberries, OWSUP позволяет легко копировать и перемещать карточки товаров между различными аккаунтами и платформами, экономя ваше время и усилия.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '0a2b5013-c95b-4534-a882-513b38d389cb', 'ru', 'Общая аналитика всех кабинетов в одном Dashboard: OWSUP предоставляет удобный обзор всех ваших аккаунтов на Ozon и Wildberries в одной панели управления, облегчая анализ и сравнение продаж.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '5a0def06-ee2f-402a-9392-4db67739b926', 'ru', 'Аналитика заказов со всех кабинетов: Получайте подробную аналитику заказов с обеих платформ, что позволит вам лучше понимать предпочтения и ожидания вашей целевой аудитории, а также принимать своевременные решения для повышения эффективности продаж.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('8cbad396-598e-435a-a225-822ec541531a', '8b98cb36-5035-4457-979f-a3380f1595d0', 'ru', 'Калькуляторы прибыли и комиссии товара: OWSUP включает в себя инструменты для расчета прибыли и комиссий, связанных с доставкой товаров и другими аспектами ведения бизнеса на платформах Ozon и Wildberries. Это позволяет продавцам точно оценить свою рентабельность и определить оптимальные стратегии ценообразования.');
