INSERT INTO projects (uuid, photo_url, title, categories) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'EPICHERISION.jpg', 'EPICHERISION 2.0', '3');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '5a831a1e-2dfa-4264-8f7d-dd10686ed5e5', 'Bot', 'NodeJs, node-html-parser');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'dcceebcb-465a-4f07-b166-b8e2f584d1fb', 'en', 'A Discord bot designed to provide information about newly arrived products. EPICHERISION is equipped with a variety of parsers for collecting data and generating ATK links, making it a valuable resource for users looking to stay informed about the latest product offerings.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'd4ffa2a5-c3f0-48fe-8fb8-a584d61e938a', 'ru', 'EPICHERISION - это интеллектуальный Discord бот, созданный для предоставления информации о поступлении новых товаров в различных магазинах. Благодаря мощным парсерам и гибким настройкам, EPICHERISION обеспечивает своевременные и релевантные оповещения, которые помогут пользователям быть в курсе последних новинок и успеть приобрести желанные товары.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'c3635b55-fa21-405e-b58e-3220bba05f52', 'ja', '新しく入荷した製品に関する情報を提供するために設計されたDiscordボット。EPICHERISIONは、データ収集とATKリンク生成のためのさまざまなパーサを備えており、最新の製品情報を追い求めるユーザーにとって貴重なリソースとなります。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '0dbb1667-20c5-4421-a23e-3cfdf13c7c55', 'ja', 'データとメタデータ用の幅広いパーサ：EPICHERISIONは、ATKリンク生成に必要な標準データとメタデータを収集するための多数のパーサを搭載しており、ユーザーが正確で最新の情報を受け取ることができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'a1a27ae9-354b-46bd-a8b3-7e15573f7a0e', 'ja', 'カスタマイズ可能なキーワードとカテゴリ選択：ユーザーは、製品選択のためのキーワードとカテゴリを編集でき、ボットの通知を自分の特定の興味や好みに合わせることができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'df498241-2c85-4870-a217-c3bba676effe', 'ja', '各ストアごとのチャンネル固有の通知：ボットは各ストアごとに別々のチャンネルに製品の更新情報を送信し、ユーザーが必要な情報に簡単にアクセスし、追跡できるようにします。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'a53781e3-0196-4284-94fc-3d7631eeb04f', 'ja', 'NikeのオンデマンドATKリンク生成：EPICHERISIONは、管理者がアクティベーションすると、Nike製品のATKリンクを生成できる機能を備えており、ユーザーはショッピング体験をさらにコントロールできます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '3f169e5b-0c24-4dce-bc92-065ef2f706f6', 'en', 'Extensive parsers for data and metadata: EPICHERISION is built with numerous parsers to gather both standard data and metadata necessary for generating ATK links, ensuring that users receive accurate and up-to-date information.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'b698ca20-a6f6-4dcf-918c-bb9da3c5aa07', 'en', 'Customizable keyword and category selection: Users can edit keywords and categories for product selection, allowing them to tailor the bot''s notifications to their specific interests and preferences.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'c97453d6-8f8a-4b8d-9907-0bb2e158549a', 'en', 'Channel-specific notifications for each store: The bot sends product updates to separate channels for each store, ensuring that users can easily access and track the information they need.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'c71934de-71a1-4343-8efe-f0421178457d', 'en', 'On-demand ATK link generation for Nike: EPICHERISION is capable of generating ATK links for Nike products upon administrator activation, providing users with even greater control over their shopping experience.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'a472422c-4f1e-4d64-922e-2877481df6eb', 'ru', 'Мощные парсеры данных: В процессе реализации EPICHERISION было создано множество парсеров, обрабатывающих как обычные, так и метаданные, необходимые для генерации АТК ссылок.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '71d21691-b646-482d-af4d-5ad2446a811f', 'ru', 'Гибкая настройка ключевых слов и категорий: Пользователи имеют возможность редактировать ключевые слова и категории товаров, которые они хотят отслеживать, получая только релевантные оповещения.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', 'd2751550-7ccd-4ae9-90e3-cf0ac835ccb4', 'ru', 'Отдельные каналы для разных магазинов: Оповещения о новых товарах отправляются в отдельные каналы для каждого магазина, обеспечивая удобную и структурированную информацию.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '0c4d567e-c277-4a0a-8208-6e0b831919c9', 'ru', 'Генерация АТК ссылок для Nike: EPICHERISION предоставляет специализированный функционал для генерации атк ссылок для магазина Nike, активируемый администратором сервера.');
