INSERT INTO subtitles( project_uuid, uuid, lang, content) VALUES ('aec0435f-9ffe-4eed-9f09-2922b70cae16', '3db30b40-701d-454a-a4f8-a54758853865', 'ru', 'Discord бот для оповещений о новых товарах');
