INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', 'eb300ef5-f7cb-496c-a2b2-80aa49f6f525', 'Front-end', 'React, Redux ToolKit');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '656cdc7b-a44b-4c81-b21b-b576140c3263', 'Back-end', 'GoLang, Gin, Gorm, PostgreSQL');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '464ba9da-b511-4a0e-81f3-8e2359155bbf', 'en', 'A specialized content management system (CMS) for portfolio websites, written in Go. Go Portfolio is designed to showcase your work in a visually appealing and professional manner, offering a range of features to help you create the perfect online portfolio.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', 'cdf591f3-b110-4633-8998-f3d451a7e225', 'ru', 'Go Portfolio - это узкоспециализированный движок для создания сайтов-портфолио, разработанный на языке программирования Go. Простой и мощный, Go Portfolio предоставляет удобную CMS для управления вашим портфолио, сохранения изображений высокого качества и отслеживания популярности проектов.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', 'a2507337-d912-43e9-a98d-1392fc689bee', 'ja', 'Go言語で作成されたポートフォリオWebサイトのための専門的なコンテンツ管理システム（CMS）。Go Portfolioは、視覚的に魅力的でプロフェッショナルな方法であなたの作品を展示するように設計されており、完璧なオンラインポートフォリオを作成するのに役立つさまざまな機能を提供しています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', 'dacadd4c-3337-4fe0-a4c8-91fd8f40b339', 'ja', 'プロジェクトの高品質な画像ストレージ：Go Portfolioは、プロジェクトの高解像度画像を保存することができ、作品が最高の品質で表示されることを保証します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '5522159a-bace-4efc-8032-64d734ce67d5', 'ja', 'Webサイトの管理パネルとしてサービスを使用する：プラットフォームは管理パネルとしても機能し、Webサイトのコンテンツと外観を完全に制御できます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '1a37dc12-cbb8-4de6-aa01-8add38255c52', 'ja', 'プロジェクトの人気統計を表示：Go Portfolioは、どのプロジェクトが訪問者に最も人気があるかについての洞察を提供し、それに応じてコンテンツ戦略を調整できます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '1ef8af14-7529-4f1c-b9c9-b2f596492f50', 'en', 'High-quality image storage for projects: Go Portfolio allows you to store high-resolution images for your projects, ensuring that your work is displayed in the best possible quality.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '7ed1601c-5cc8-4a8b-851a-008585cdde16', 'en', 'Use the service as an admin panel for your website: The platform can also function as an admin panel, giving you full control over the content and appearance of your website.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '723e5788-6ac5-4add-b5a4-915f5bcef507', 'en', 'View project popularity statistics: Go Portfolio provides insights into which projects are the most popular among visitors, allowing you to tailor your content strategy accordingly.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '6c1cd62f-422b-4c01-8c5d-8f74268fa5cc', 'ru', 'Хранение изображений высокого качества: Go Portfolio позволяет сохранять изображения ваших проектов в высоком качестве, гарантируя яркое и четкое воспроизведение на вашем сайте.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', 'b0766bfb-0834-4a64-9816-2f81f6412493', 'ru', 'Интуитивная админ-панель: Сервис предоставляет удобную и простую в использовании админ-панель для управления содержимым вашего сайта-портфолио.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '22feef98-c64b-4a97-b9de-a4f52e7940eb', 'ru', 'Статистика популярности проектов: Go Portfolio позволяет отслеживать популярность различных проектов на вашем сайте, предоставляя статистику о просмотрах и взаимодействиях посетителей.');
INSERT INTO photos (project_uuid, uuid, path) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', 'b83aeef5-1032-4cc5-adab-86d54f024299', 'just-portfolio/1.jpg');
INSERT INTO photos (project_uuid, uuid, path) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '6e4ee90f-1a0d-423d-a9de-5c2dff12432a', 'just-portfolio/2.jpg');
