INSERT INTO subtitles( project_uuid, uuid, lang, content) VALUES ('57fe52eb-04d2-4d09-8d9d-29af4f916ef7', '37bab658-af72-440a-90df-88b9fd674919', 'ru', 'Go Portfolio - движок для сайтов-портфолио на языке Go');
