INSERT INTO projects (uuid, photo_url, title, categories) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'EMIAS.jpg', 'EMIAS', '2');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '43eadade-03f6-4864-a73b-1a07bc6335e6', 'Front-end', 'Vue, Vue Router');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'bab43fc3-3993-47f3-b963-f30de1683aef', 'Back-end', 'NodeJs, Express, MySQL');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'e128c818-7949-468c-95a6-08a3cdc232e7', 'en', 'A web platform for EMIAS company employees, designed to facilitate efficient task management and provide real-time updates on task statuses. The platform streamlines workflows and fosters collaboration among team members, enabling them to work more effectively and achieve their goals.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'fe2728be-c1d9-443c-867f-f3a339133c5c', 'ru', 'EMIAS - это уникальная веб-платформа, разработанная специально для сотрудников компании EMIAS. Платформа предоставляет интуитивные инструменты для эффективного управления задачами, отслеживания прогресса команды и предложения идей. EMIAS также включает в себя систему наград и значков, поощряющую активность и достижения сотрудников.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '454e66e1-3b78-43b4-b257-a0e1048ef00e', 'ja', 'EMIAS社の従業員向けに設計されたウェブプラットフォームは、効率的なタスク管理を促進し、タスクのステータスに関するリアルタイムの更新を提供します。プラットフォームはワークフローを合理化し、チームメンバー間の協力を促進し、彼らがより効果的に働き、目標を達成することができるようにします。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '327aa9c7-b5d7-4014-83d4-0d26d18f22d7', 'ja', '従業員のためのパーソナルダッシュボード：EMIASは、各従業員のパーソナルダッシュボードを備えており、タスクを管理し、整理された状態を維持することができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '25212f79-9878-4739-876a-5a3e6fcdccb0', 'ja', 'アイデア提出機能：チームメンバーは自分のアイデアを提案することができ、協力的で革新的な作業環境を促進します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '214e805a-e3d0-4c6f-a901-8025bbeb0f26', 'ja', 'チームの進捗状況の追跡：従業員はチームの進捗状況を監視し、プロジェクト内の最新の動向に常に最新情報を保つことができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'dd64ccd7-0373-4294-aaa2-492c2fe091e7', 'ja', '功績のあるユーザーにバッジを授与する：プラットフォームは、管理者が特別なバッジを授与することで、優れた従業員を認識し、報われることができます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'cfc4a8a4-48e9-46cc-b058-868b02def71f', 'en', 'Personal dashboard for employees: EMIAS features a personal dashboard for each employee, allowing them to manage their tasks and stay organized.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'd37fe5af-a28f-4985-ae70-99c9494103b1', 'en', 'Idea submission capability: Team members can propose their own ideas, fostering a collaborative and innovative work environment.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '23980e09-bd9a-4ee5-a472-92f10f15293f', 'en', 'Team progress tracking: Employees can monitor their team''s progress and stay up-to-date on the latest developments within their projects.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '8e20adc6-b37f-4572-865a-52c9a6d585d0', 'en', 'Awarding badges to deserving users: The platform allows administrators to recognize and reward outstanding employees by granting them special badges.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'a9a2eb53-d540-4f42-8660-8b9bafd1a148', 'ru', 'Личный кабинет для сотрудников: Каждый сотрудник имеет доступ к своему личному кабинету, где он может управлять своими задачами, отслеживать выполнение и следить за актуальным статусом работы.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'f321549a-0f55-414c-87f8-32eb544e743d', 'ru', 'Возможность предлагать идеи: Сотрудники могут предлагать свои идеи для улучшения процессов, продуктов или услуг компании, обсуждать их с коллегами и реализовывать наиболее перспективные предложения.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', 'a6712ed2-5cd8-4bea-b34a-6a7be6d604c7', 'ru', 'Просмотр прогресса команды: Платформа предоставляет возможность просматривать общий прогресс команды, отслеживать выполнение задач и сравнивать результаты разных подразделений.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('f259e99b-4722-4646-9c48-0de63e6beed4', '71b7e7ab-d3b9-4177-afa7-16d4e23e4dfe', 'ru', 'Система наград и значков: EMIAS включает систему значков, которые могут быть присвоены сотрудникам за особые достижения, активность или вклад в развитие компании. Это способствует мотивации и вовлеченности персонала.');
