INSERT INTO projects (uuid, photo_url, title, categories) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', 'UBusiness.jpg', 'U.Business', '2');
INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '3802b23b-aaec-4747-a1b7-30e82550b244', 'Front-end', 'NextJs, i18n');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '5611cf8b-745a-45c7-ad09-b583f8c80861', 'en', 'A multilingual website built using Next.js for a company. U.Business features an admin panel for easy text editing, providing a user-friendly experience for both site administrators and visitors.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '98802a7a-73d8-452d-bcce-e97a25751200', 'ru', 'U.Business - это современный и функциональный сайт, разработанный на next.js для компании, предоставляющий мультиязычность и удобную админ-панель для редактирования текста. Сайт обеспечивает гибкость и адаптивность, позволяя компании обращаться к международному рынку с помощью интуитивно понятного и простого в управлении веб-ресурса.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '3fd49416-6b54-4b36-b682-94c666100964', 'ja', '企業向けにNext.jsを使用して構築された多言語ウェブサイトです。U.Businessは、テキスト編集が簡単な管理パネルを備えており、サイト管理者と訪問者の両方にとって使いやすい体験を提供します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '841f1131-e40a-418c-af93-5dfb6277b9b6', 'ja', 'テキスト編集用の管理パネル：U.Businessには、ウェブサイトのコンテンツが最新かつ関連性のある状態を維持できるように、シンプルなテキスト編集が可能な管理パネルが含まれています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '7a730ae2-fff6-4737-a925-b61f7f3c2213', 'ja', 'i18nを使用した多言語対応：ウェブサイトはi18nで構築されており、複数の言語をシームレスにサポートし、多様なユーザーに対応しています。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '3f1dc7f3-19ed-4413-a2f4-8b3e4b03518a', 'en', 'Admin panel for text editing: U.Business includes an admin panel that allows for simple text editing, ensuring that the website''s content remains up-to-date and relevant.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '0adbee17-dffe-4f58-bd44-0acbf82bcf2f', 'en', 'Multilingual support with i18n: The website is built with i18n, enabling seamless support for multiple languages and catering to a diverse range of users.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', '5c535e51-0fa9-4246-8e76-83f9746f6f15', 'ru', 'Админ-панель для редактирования текста: U.Business предоставляет удобную и простую в использовании админ-панель, которая позволяет редактировать и обновлять текст на сайте без необходимости обращаться к разработчикам.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('cde03dbb-30ff-4704-91d7-20d8466159c1', 'b2d9f65e-656f-4424-a038-a3b44edf85e7', 'ru', 'Мультиязычность с помощью i18n: Благодаря интеграции с библиотекой i18n, сайт поддерживает мультиязычность и позволяет компании легко добавлять новые языковые версии, адаптируясь к потребностям международного рынка.');
