INSERT INTO cards (project_uuid,  uuid,  title,  sub_title) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '30612132-f780-4622-9bf6-0d0ba5897085', 'Back-end', 'Python 3, Flask, MySQL');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '4e43c45b-a2de-461a-ad4d-3bbd7a767561', 'en', 'A "Secret Santa" game designed for the CROC company. This traditional Christmas game involves anonymous gift exchanges among a group of participating individuals, bringing fun and festive cheer to the workplace.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '10b18d4f-fa29-43ef-89ac-1689c16d44ea', 'ru', 'Secret Santa - это онлайн-версия традиционной рождественской игры "Тайный Санта" для компании Croc. Цель игры - анонимный обмен подарками между участниками, работающими в разных отделах и специализациях. Secret Santa предоставляет удобный личный кабинет для управления всеми аспектами игры, а также интеллектуальный алгоритм подбора пар и рассылку писем на почту участников.');
INSERT INTO descriptions (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '934f615b-1652-4319-96fe-d8e12abc5604', 'ja', 'CROC社向けにデザインされた「シークレットサンタ」というゲームです。この伝統的なクリスマスゲームは、参加者が匿名でプレゼントを交換することで、職場に楽しさと祝祭の喜びをもたらします。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '5cd67582-1f95-4a86-be66-56aed8c8f999', 'ja', '多様なペアリングを作成するアルゴリズム：このゲームでは、独自のアルゴリズムを使用して、異なる専門分野や部門の参加者同士のペアを作成し、すべてのプレイヤーに多様でエンゲージングな体験を提供します。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '2258a89e-3038-4673-a700-9936cf28b631', 'ja', 'メール通知：シークレットサンタはメール通知を送信して参加者に情報を提供し、ゲーム全体を通じてスムーズなコミュニケーションが行われるようにします。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'e81c093d-f2cd-410e-88b4-6e2e7c21ae59', 'ja', '個人用ダッシュボード機能：すべてのゲーム機能は、各参加者の個人用ダッシュボードからアクセスできるため、プレイヤーがゲームへの関与を簡単に管理できます。');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '1409e7e2-02c3-4a22-98e6-6933130891a5', 'en', 'Algorithm for creating diverse pairings: The game uses a unique algorithm to create pairs of participants from different specializations and departments, ensuring a diverse and engaging experience for all players.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '5cb00653-2ced-44ff-b41e-31c9f7c6ee30', 'en', 'Email notifications: Secret Santa sends out email notifications to keep participants informed and ensure smooth communication throughout the game.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '7f0841ef-d39b-4af0-9ed9-61c4d2a730aa', 'en', 'Personal dashboard features: All game functions can be accessed through each participant''s personal dashboard, making it easy for players to manage their involvement in the game.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '0e6d9b58-ca3e-46e3-8de3-1863bc4836b8', 'ru', 'Интеллектуальный алгоритм подбора пар: Secret Santa использует алгоритм, который создает пары для обмена подарками, учитывая разные специализации и отделы участников. Это способствует интерактивности и интеграции сотрудников компании');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '82091bc0-152c-43cc-8719-cccc3c870869', 'ru', 'Рассылка писем на почту: Все уведомления об игре, включая информацию о вашем "Тайном Санте" и его предпочтениях, отправляются участникам на электронную почту.');
INSERT INTO features (project_uuid, uuid, lang, content) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', 'cd68f0ac-f420-44ef-bef0-0feafa21cc88', 'ru', 'Личный кабинет: Secret Santa предоставляет удобный личный кабинет, в котором участники могут управлять своим участием в игре, просматривать информацию о своем "Тайном Санте" и получать уведомления о событиях.');
INSERT INTO photos (project_uuid, uuid, path) VALUES ('214d486e-0b5c-4f19-8a25-7399cf7bc18c', '641719c0-f829-49c3-936a-0d3db291367c', 'secret-santa/1.jpg');
